import React from "react";
import "./Libcreate.css";
import { useState } from "react";

function Libcreate() {
  const [libname, setLibName] = useState("");
  const [ownername, setOwnerName] = useState("");
  const [gmail, setGmail]=useState("");
  const [contactnumber, setContactNumber] = useState("");

  const [password, setPassword]=useState("");
  const [message, setMessage] = useState("");

  let handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let res = await fetch("http://localhost:8080/library", {
        method: "POST",
        body: JSON.stringify({
          libname: libname,
          ownername: ownername,
          gmail: gmail,
          contactnumber: Number(contactnumber),

          password: password,
        }),
      });
      console.log(res)
      let resJson = await res.json();
      if (res.status === 200) {

        setOwnerName("");
        setGmail("");
        setContactNumber("");
        setLibId("");
        setPassword("");
        setMessage("User created successfully");
      } else {
        setMessage("Some error occured");
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className="project">
      <div className="login1">
      <h4>Create Library</h4>
      <form onSubmit={handleSubmit}>

      <div   className="text_area">
      {/* <label htmlFor="gmail">Owner Name:</label> */}
      <input
       type="text"
       value={ownername}
       placeholder="Write The OwnerName"
       onChange={(e) => setOwnerName(e.target.value)}
        className="text_input2"
      />
      </div>

     <div className="text_area" >
     {/* <label htmlFor="gmail">Onwer Gmail:</label> */}
     <input
        type="gmail"
        value={gmail}
        placeholder="Write the Owner Gmail"
        onChange={(e) => setGmail(e.target.value)}
        className="text_input2"
      />
     </div>

     <div className="text_area" >
     {/* <label htmlFor="contactNumber">Contact Number:</label> */}
     <input
        type="tel"
        value={contactnumber}
        placeholder="Write Your Contact Number"
        onChange={(e) => setContactNumber(e.target.value)}
        pattern="[0-9]{10}"
        // defaultValue="Email@example.com"
        className="text_input2"
      />
     </div>




     <div className="text_area" >
     {/* <label htmlFor="password">Password:</label> */}
     <input
        type="password"
        value={password}
        placeholder="Create Your Password"
        onChange={(e) => setPassword(e.target.value)}
        className="text_input2"
      />
     </div>

     <div   className="text_area">
      {/* <label htmlFor="gmail">Library Name:</label> */}
      <input
        type="text"
        value={libname}
        placeholder="Write library name here.."
        onChange={(e) => setLibName(e.target.value)}
        className="text_input2"
      />
      </div>

      <input
          type="submit"
          value="Cteate Library"
          className="btn"

        />
    </form>
      </div>
    </div>
  );
}
export default Libcreate;
