import { useRef } from "react";
import "./Adminheader.css";
import { Link } from "react-router-dom";
import { FaBars, FaTimes } from "react-icons/fa";

function Adminheader() {
  const navRef = useRef();

  const showNavbar = () => {
    navRef.current.classList.toggle("responsive_nav");
  };

  return (
    <div className="navbar">
      <header>
        <Link to="/">
          <img
            className="header_logo"
            src="https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.xenonstack.com%2Fblog&psig=AOvVaw0Iu21mqQaK206mxDV2PIuO&ust=1711871573232000&source=images&cd=vfe&opi=89978449&ved=0CBIQjRxqFwoTCLj2rdzAm4UDFQAAAAAdAAAAABAE"
            alt="header_logo"
          />
        </Link>

        <nav ref={navRef}>
          <Link t onClick={showNavbar} o="/" className="nav-element">
            <p>Admin Deshboard</p>
          </Link>

          <Link onClick={showNavbar} to="/liblist" className="nav-element">
            <p>Add book</p>
          </Link>

          <Link onClick={showNavbar} to="/libcreate" className="nav-element">
            <p>Delete  Book</p>
          </Link>

          <Link onClick={showNavbar} to="/requestaccess" className="nav-element">
            <p>Update Book</p>
          </Link>
          <Link onClick={showNavbar} to="/requestaccess" className="nav-element">
            <p>See  Requests</p>
          </Link>




          {/* <Link to="/login" className="nav-element">
            <div className="signin_but">
              <p>Sign In</p>
            </div>
          </Link> */}

          <button className="nav-btn nav-close-btn" onClick={showNavbar}>
            <FaTimes />
          </button>
        </nav>

        <button className="nav-btn" onClick={showNavbar}>
          <FaBars />
        </button>
      </header>
    </div>
  );
}

export default Adminheader;
