import React, { useEffect, useState } from 'react'
import "./liblist.css";


function Liblist() {
  const url = "http://localhost:9999/library";
  const [data, setData] = useState([]);


  const  fetchInfo= () => {
    return fetch(url)
      .then((res) => res.json())
      .then((d) => setData(d))
  }


  useEffect(() => {
    fetchInfo();
  }, []);
  return (

    <div className="App">
      <h1 style={{ color: "green" }}>Available Libraries</h1>
      <center>
        {data.map((dataObj, index) => {
          return (
            <div
              style={{
                width: "15em",
                backgroundColor: "#35D841",
                padding: 2,
                borderRadius: 10,
                marginBlock: 10,
              }}
            >
              <p style={{ fontSize: 20, color: 'white' }}>{dataObj.name,dataObj.index}</p>
            </div>
          );
        })}
      </center>
    </div>
  )
}
export default Liblist
