import React, { useEffect, useState } from 'react'
import "./liblist.css";


function Liblist() {
  const url = "http://localhost:9999/library";
  const [data, setData] = useState([]);


  const  fetchInfo= () => {
    return fetch(url)
      .then((res) => res.json())
      .then((d) => setData(d))
  }


  useEffect(() => {
    fetchInfo();
  }, []);
  return (

    <div className="App">
      <h1  className='text'>Available Libraries</h1>
      <center>
        {data.map((dataObj, index) => {
          return (
            <div className=''>
                    <div >
                           <p className='text2'>Library Name: {dataObj.name} <br/> id = {dataObj.ID}</p>
                   </div>
            </div>

          );
        })}
      </center>
    </div>
  )
}
export default Liblist
