import React, { useEffect, useState } from 'react'
import "./Alluser.css";
import { GetUserCall } from '../hooks/apicall';


function AllBook() {
    // const url = "http://localhost:9999/owner/user";
    const [data, setData] = useState([]);


    // const fetchInfo = () => {
    //     return fetch(url)
    //         .then((res) => res.json())
    //         .then((d) => setData(d))
    // }


    useEffect(() => {
        GetBookCall("admin").then((res) => {
            setData(res.data)

        }).catch((res) => {
            setData(null)
        })
    }, []);
    return (

        <div className="App">
            <h1 className='text'>All BOOKS</h1>
            <center>
                {data.map((dataObj, index) => {
                    return (
                        <div className=''>
                            <div >
                                <p className='text3'> ISBN : {dataObj.isbn} <br/>Title: {dataObj.title} <br/>  Authors :{dataObj.authors} 
                                <br/> Publisher :{dataObj.publisher}
                                <br/> Version: {dataObj.version}
                                <br/> Total Copies : {dataObj.total_copies}</p>
                            </div>
                        </div>

                    );
                })}
            </center>
        </div>
    )
}
export default AllBook
